import React from "react";
import "./contact.css"
import { useRef } from 'react';
import emailjs from "emailjs-com"
// import icons
// import {MdOutlineEmail} from "react-icons/md"
// import {BsWhatsapp} from "react-icons/bs"

const Contact = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_7vzfwpa', 'template_tj1ic8a', form.current, 'fbyQUwgSEd0vBKh6p')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <section id="contact">
      <h2>Contact Me</h2>

      <div className="container contact__container">
        <div className="contact__options">
          <article className="contact__option">
            {/* <MdOutlineEmail classname="contact__option-icon"/> */}
            <h4>Email</h4>
            <h5>Indrajeet.patwardhan@gmail.com</h5>
            <a href="mailto:Indrajeet.patwardhan@gmail.com" target="_blank">Send a message</a>
          </article>
          <article className="contact__option">
            {/* <BsWhatsapp classname="contact__option-icon"/> */}
            <h4>Whatsapp</h4>
            <h5>9498729524</h5>
            <a href="https://api.whatsapp.com/send?phone=9498729524" target="_blank">Send a message</a>
          </article>
        </div>
        {/* END OF CONTACT OPTIONS */}
        <form ref={form} onSubmit={sendEmail}>
          <input type="text" name="name" placeholder="Your Full Name" required />
          <input type="email" name="email" placeholder="Your Email" required />
          <textarea name="message" rows="7" placeholder="Your Message" required ></textarea>
          <button type="submit" className="btn btn-primary">Send Message</button>
        </form>
      </div>
    </section>
  )
}

export default Contact
